import React, {useState} from "react";


const Input3 = () => {
    const [inputs, setInputs] = useState(
        {
            name: ""
            , email: ""
        }
    );

    const {name, email} = inputs;

    const onChangeCall = (e) => {
        const value = e.target.value;
        const id = e.target.id;

        setInputs(
            {
                ...inputs
                ,[id]: value
            }
        )
        // setInputs(
        //     {...inputs
        //         , [id]:value}
        // )
    };


    return(
        <div>

                <div>이름 : <input type="text" id="name" value={inputs.name} onChage={onChangeCall}></input></div>
                <div>이메일 : <input type="text" id="email" value={inputs.email} onChage={onChangeCall}></input></div>

        </div>
    )


}

export default Input3;
