        , tel: "010-1234-5678"
    });

    const onChangeCall = (e) => {
        const value = e.target.value;
        const id  = e.target.id;

        setInputs(
            {
                ...inputs,
                [id]: value
            }
        )
    };

    return (
            <div>
                <div>
                    <label>이름</label>
                    <input type="text" id="fullname" value={fullname} />
                </div>

                <div>
                    <label>이메일 </label>
                    <input type="text" id="email" value={tel}/>
                </div>
            </div>
    );
}

