import React, {useState} from "react";


const Input3 = () => {
    const [inputs, setInput] = useState(
        {
            name: ""
            , email: ""
        }
    );

    const {name, email} = inputs;

    const onChangeCall = (e) => {
        const value = e.target.value;
        const id = e.target.id;


        setInput(
            {
                ...inputs
                , [id]:value
            }
        )
    };


    return(
        <div>
        <div>

                <div>이름 : <input type="text" id="name" value={inputs.name} onChange={onChangeCall}></input></div>
                <div>이메일 : <input type="text" id="email" value={inputs.email} onChange={onChangeCall}></input></div>

        </div>

        <div>
            <p>이름 :{inputs.name}</p>
            <p>이메일 :{inputs.name}</p>
        </div>

        </div>
    )


}

export default Input3;
