import React, {useState} from "react";

const Input2 = () => {

    const [number, setNum] = useState(0);

    const [inputs, setInputs] = useState({
          fullname: ""
        , email: ""
    });

    //--변수 추출, 화면에서 변수사용  or  inputs.fullname
    const{fullname, email} = inputs;

    const onChangeCall = (e) => {
        const value = e.target.value;
        const id  = e.target.id;

        // ...inputs  스프레드  New 와같은 복제
        setInputs(
            {
                ...inputs,
                [id]: value
            }
        )


        setNum(2);
    };

    return (
            <div>
                <div>
                    <label>이름</label>
                    <input type="text" id="fullname" value={inputs.fullname} onChange={onChangeCall}/>
                </div>

                <div>
                    <label>이메일 </label>
                    <input type="text" id="email" value={email} onChange={onChangeCall}/>
                </div>

                <p>이름 : {fullname}</p>
                <p>이메일 : {email}</p>
            </div>
        );
}

export default Input2;
