import React , {useState} from "react";


const Input4 = () =>
{
    const[inputs, setInputs] = useState(
        {
            name: ""
            , email: ""
        }
    );

    const {name, email} = inputs;


    const onChangeCall = (e) => {
        const value = e.target.value;
        const id = e.target.id;

        setInputs(
            {
                ...inputs
                , [id]:value
            }
        );
    };

    return (
        <div>
            <p> 이름 : <input type="text" id="name" value={name} onChange={onChangeCall}/></p>
            <p> 이메일 : <input type="text" id="email" value={email} onChange={onChangeCall} /></p>
            <p> 이름 {name}</p>
            <p> 이메일 {email}</p>
        </div>
    );
}

export default Input4;
